function fib(n) {
    if (n < 0) return "Invalid input. Please provide a positive integer.";

    let fibs = [0, 1];
    let currentFib = 0;

    while (true) {
        const nextFib = fibs[currentFib] + fibs[currentFib - 1];

        if (nextFib > n) break;

        fibs.push(nextFib);
        currentFib = nextFib;
    }

    return fibs[n - 1];
}

