  // Check if x and banana are numbers
  if (typeof x !== 'number' || typeof banana !== 'number') {
    throw new Error("Both inputs must be numbers");
  }

  // If the GCD of x and banana is 0, return 0
  if (x === 0 || banana === 0) {
    return 0;
  }

  // Calculate the GCD using Euclidean algorithm
  let gcd = x;
  while (gcd !== 0) {
    const remainder = gcd % banana;
    gcd = banana;
    banana = remainder;
  }

  return gcd;
}

