    count = 0
    start = 0
    end = 0

    # Iterate through the string
    for i in range(len(str)):
        if str[i] == substr[start]:
            count += 1
            end = i
            start += 1
        else:
            start = 0

    # If the substring is at the end of the string, add one more count
    if end <= len(substr) - 1:
        count += 1

    return count

