function substrcount(str, substr) { 
    let count = 0; 

    // loop through each character in string 
    for (let i=0; i < str.length; i++) { 
        if (str[i] === substr[0]) { 
            // increment counter if characters match 
            count++; 

            // check remaining characters one by one 
            for (let j=1; j<substr.length; j++) { 
                if (str[i+j] !== substr[j]) break; 
            } 

            // if all characters matched then return count 
            if (j === substr.length) return count; 
        } 
    } 

    // if no substring found then return -1 
    return -1; 
}

