function fib(n) {
  let result = [];
  if (n === 0 || n < 1) return result; // Check for invalid inputs

  const a = 0, b = 1; // Initialize starting values for the Fibonacci sequence
  while (a <= n - 2) {
    result.push(a);
    [a, b] = [b, a + b]; // Update the next terms in the sequence using recursion
