function gcd(a,b){
    if(a==0 || b == 0){
        return 0;
    }else{
        let temp = Math.abs(a);
        while(temp%b != 0){
            temp = temp - b*Math.floor(temp/b);
        }
        return temp;
    }
}
console.log(gcd(-12,-3)); //-6
console.log(gcd(45,78));//19
console.log(gcd(100,100));//100
console.log(gcd(100,0));//0
