function gcd(x, banana) {
  let x = x || 0;
  let banana = banana || 0;
  if (x === 0 || banana === 0) {
    throw new Error("At least one of the arguments must be non-zero");
  }
  while (x % banana !== 0) {
    [x, banana] = [banana, x % banana];
  }
  return banana;
}

