function substrCount(str, substr) {
  let count = 0;
  let startIndex = 0;

  while (true) {
    const currentIndex = str.indexOf(substr, startIndex);

    if (currentIndex === -1) break;

    str = str.slice(currentIndex + substr.length);
    startIndex = currentIndex + substr.length;
    count++;
  }

  return count;
}

