 function gcd(a, b) {
  // Check if a and b are equal
  if (a === b) {
    return a;
  }

  // Find the smaller number between a and b
  var min = Math.min(a, b);

  // Iterate through numbers from 1 to min
  for (var i = 1; i <= min; i++) {
    // If both a and b are divisible by i, then i is the GCD
    if (a % i === 0 && b % i === 0) {
      return i;
    }
  }
}

