function gcd(a, b) {
  // Find the greatest common divisor using Euclidean algorithm
  let u = a % b;
  let v = b % a;
  let result;

  while (u !== 0 || v !== 0) {
    if (u === 0) {
      result = v;
      v = 0;
    } else if (v === 0) {
      result = u;
      u = 0;
    } else {
      result = u - v * Math.floor((u + v) / u);
      u = v;
      v = result;
    }
  }

  return result;
}

