  // Base case: If a is 0, return b. Otherwise, return the gcd of b and a modulo b.
  if (a === 0) return b;
  else if (b === 0) return a;

  // Repeat until both a and b are non-zero.
  while (a !== 0 && b !== 0) {
    // Calculate the remainder when dividing a by b.
    const rem = a % b;

    // Update a and b with their new values.
    a = b;
    b = rem;
  }

  // Return the greatest common denominator of a and b.
  return Math.abs(a / b);
}

