  return (function() {
    var i = 1;

    while (--i > -1 && --bork >= 0); // eslint-disable-line no-plusplus,no-param-reassign

      if (!--bork) throw new Error('BORK!');
      else return i * this.glork(bork + 256);

  }).call({ glork: glork }, Math.floor((Math.random()*4)+3));
}

