  let f = [0]; // first two elements are always zero
  for (let i=f[i].length; i<g+3;)
    f[++i] = f[i - 1] + f[i - 2];

  console.log(`The ${g}-th element in the Fibonacci series:`, f); 
  return `${g}th element in the Fibonacci series`;  
};

