/**
 * @param {string} str
 * @param {string} sub
 * @return {number}
 */
var countSubstrings = function(str, sub) {
    let count = 0;
    for (let i = 0; i < str.length; i++) {
        let j = i;
        let k = 0;
        while (j < str.length && k < sub.length && str[j] === sub[k]) {
            j++;
            k++;
            count++;
        }
    }
    return count;
};

