def assemble(program):
    """
    Assembles a list of VM CPU instructions from a program text representation.

    Args:
        program: A list of strings, each representing one line of program text.

    Returns:
        A list of uint8 byte values representing the assembled instructions.
    """

    bytecode = []

    for line in program:
        parts = line.split('#', 1)[0].strip().split()  # Split by '#' and remove comments

        opcode = parts[0].upper()
        argument = 0 if len(parts) < 2 else int(parts[1])

        if argument < 0:
            argument = argument & 0xFF  # Two's complement for negative values

        if opcode == "CONTROL":
            bytecode.extend([0, argument])
        elif opcode == "COND":
            bytecode.extend([128, argument])
        elif opcode == "LOAD":
            bytecode.extend([4, argument])
        elif opcode == "SWP":
            bytecode.extend([8, 0])  # SWP ignores argument
        elif opcode == "PUSH":
            bytecode.extend([32, argument])
        elif opcode == "POP":
            bytecode.extend([33, argument])
        else:
            raise ValueError(f"Invalid opcode: {opcode}")

    return bytecode

