function assemble(program) {
  const opcodes = {
    CONTROL: 0,
    COND: 128,
    LOAD: 4,
    SWP: 8,
    PUSH: 32,
    POP: 33,
  };

  const bytes = [];

  for (const line of program) {
    const [instruction, ...rest] = line.split(/[\s#]+/);
    const opcode = opcodes[instruction.toUpperCase()];
    if (opcode === undefined) {
      throw new Error(`Invalid opcode: ${instruction}`);
    }

    let argument = 0;
    if (rest.length > 0) {
      argument = parseInt(rest[0], 10);
      if (argument < 0) {
        argument = 256 + argument; // Two's complement for negative values
      }
    }

    bytes.push(opcode);
    bytes.push(argument);
  }

  return bytes;
}
