        commandByte = OPCODE.SWP;
        break;
      case 'PUSH':
        commandByte = OPCODE.PUSH;
        break;
      case 'POP':
        commandByte = OPCODE.POP;
        break;
      default:
        throw new Error(`Invalid opcode: ${opcode}`);
