function substrCount(str, substr) {
  // Initialize the count variable to 0.
  let count = 0;

  // Iterate through each character in the string 'str'.
  for (let i = 0; i < str.length; i++) {

    // Check if the current character is equal to the sub-string 'substr'.
    if (str[i] == substr) {
      // Increment the count variable by 1.
      count++;
    }
  }

  // Return the final count value.
  return count;
}

