def flatten(obj, parent_key='', sep='.'):
    items = {}
    for k, v in obj.items():
        new_key = f"{parent_key}{sep}{k}" if parent_key else k

        # If the value is a simple type (int, float, str), add it as-is
        if not isinstance(v, (dict, list)):
            items[new_key] = v
            continue

        # Recursively flatten dictionaries and lists
        if isinstance(v, dict):
            sub_items = flatten(v, new_key, sep=sep)
            for sub_k, sub_v in sub_items.items():
                items[sub_k] = sub_v

        elif isinstance(v, list):
            # Flatten lists by converting them into tuples with index and value
            flattened_list = [(f"{new_key}{sep}{i}", item) for i, item in enumerate(v)]
            items.update({k: v for k, v in flattened_list})

    return items
