function assemble(program) {
    const opcodeTable = {
        'CONTROL': 0x00,
        'COND': 0x80,
        'LOAD': 0x40,
        'SWP': 0xC0,
        'PUSH': 0x20,
        'POP': 0x33
    };

    function convertToUint8(value) {
        return value < 0 ? (Math.pow(2, 8) + value) >>> 0 : value;
    }

    const instructions = program.map((line) => {
        let [opcode, arg] = line.split(' ');
        opcode = opcode.toUpperCase();
        if (!opcodeTable[opcode]) throw new Error(`Invalid OPCODE: ${opcode}`);

        const commandByte = opcodeTable[opcode];
        let argumentByte;

        // Ignore comments and default to 0 for arguments
        if (arg.startsWith('#')) {
            arg = '';
        } else {
            argumentByte = convertToUint8(parseInt(arg, 10));
        }

        return [commandByte, argumentByte];
    });

    return instructions;
}
