function assemble(program) {
  const opcodes = {
    CONTROL: 0,
    COND: 128,
    LOAD: 4,
    SWP: 8,
    PUSH: 32,
    POP: 33
  };

  const bytes = [];

  for (let line of program) {
    const parts = line.split(' ');
    const opcode = parts[0];
    let arg = 0;

    if (parts.length > 1) {
      arg = parseInt(parts[1]);
      if (arg < 0) {
        arg = 256 + (arg % 256); // Convert negative values to uint8
      }
    }

    if (opcode in opcodes) {
      bytes.push(opcodes[opcode]);
      bytes.push(arg);
    }
  }

  return bytes;
}
```

