};

function assemble(program) {
  const instructions = [];
  for (const line of program) {
    // Extract the OPCODE and argument from the line
    const match = line.match(/^(\w+)\s*(\d*)\s*#?\s*(.*)$/);
    if (!match) continue; // Skip lines that don't match the pattern
