    const opcode = OPCODE_MAP[opcodeStr];
    let argument = parseInt(argStr || '0', 10);

    // Convert negative argument values to uint8 (twos complement)
    if (argument < 0) {
      argument += Math.pow(2, 8);
    }
    argument &= 0xFF; // Ensure argument is within the range of a uint8

    // Add the instruction to the list
    instructions.push(opcode, argument);
  }
  return instructions;
}
