function substrcount(str, substr) {
  let count = 0;
  for (let i = 0; i < str.length; i++) {
    if (str[i] === substr[0]) {
      let j = 1;
      while (j < substr.length && str[i + j] === substr[j]) {
        j++;
      }
      if (j === substr.length) {
        count++;
      }
    }
  }
  return count;
}
