function substrcount(str, substr) {
  var count = 0;
  var len = str.length;
  var substrLen = substr.length;
  for (var i = 0; i <= len - substrLen; i++) {
    if (str.substring(i, i + substrLen) === substr) {
      count++;
    }
  }
  return count;
}
