```javascript
function substrcount(str, substr) {
  let count = 0;
  let substrLen = substr.length;

  for (let i = 0; i <= str.length - substrLen; i++) {
    if (str.substring(i, i + substrLen) === substr) {
      count++;
    }
  }

  return count;
}

